    /***
     * map
     * 定位
     * @param view
     */
    public void mapLocation(View view){
        AllAmap.myMap.moveCamera(CameraUpdateFactory.changeLatLng(new LatLng(AllAmap.myMap.getMyLocation().getLatitude(), AllAmap.myMap.getMyLocation().getLongitude())));
    }

